data.css:
html, body {
    width: 100%;
    height: 100%;
    text-align: center;
    overflow: hidden;
    position: relative;
    animation: fade-bg 1s ease;
    background: #111;

}

.container {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
}

.quote {
    font-size: 200%;
    opacity: .3;
    color: white;
    display: block;
    line-height: 1.2;
    padding-bottom: 1rem;
    animation: fade-quote .3s ease;
}
.source {
    font-size: 150%;
    opacity: .25;
    color: white;
    animation: fade-source .4s ease;
}

@keyframes fade-bg {
    from { background: #000; }
    to   { background: #111; }
}


@keyframes fade-quote {
    from { opacity: 0; }
    to   { opacity: .3; }
}

@keyframes fade-source {
    from { opacity: 0; }
    to   { opacity: .25; }
}
