data.css:
html, body {
    width: 100%;
    height: 100%;
    text-align: center;
    overflow: hidden;
    position: relative;
    background: #0a0a0a;
    animation: fade-bg .5s ease;
}

.container {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
}

.quote {
    font-size: 200%;
    opacity: .3;
    color: white;
    display: block;
    line-height: 1.2;
    padding-bottom: 1rem;
    animation: fade-quote .3s ease;
}
.source {
    font-size: 150%;
    opacity: .25;
    color: white;
    animation: fade-source .5s ease;
}

@keyframes fade-bg {
    from { background: #111; }
    to   { background: #0a0a0a; }
}


@keyframes fade-quote {
    from { opacity: 0; transform: translateY(.375rem); }
    to   { opacity: .3; transform: translateY(0); }
}

@keyframes fade-source {
    from { opacity: 0; transform: translateY(.5rem); }
    to   { opacity: .25; transform: translateY(0); }
}
